  var player = videojs('my-video');

  player.theaterMode({ elementToToggle: 'page');

  player.on('theaterMode', function(elm, data) {
    if (data.theaterModeIsOn) {
      $('#page').turnOnTheaterMode(); // or whatever
    } else {
      $('#page').turnOffTheaterMode();
    }
  });
